mapper.select:
select * from merchant_pay_order
where  merchant_id = #{merchantPayOrder.merchantId}

 and message != "SUCCESS" 
 and message = "SUCCESS" 


 and create_time between #{merchantPayOrder.startDate} and #{merchantPayOrder.endDate}


 and status = #{merchantPayOrder.status}

 and address like concat("%", #{merchantPayOrder.address},"%")
 and return_address like concat("%", #{merchantPayOrder.returnAddress},"%")
 and merchant_order_no like concat("%",#{merchantPayOrder.merchantOrderNo},"%") 
 and have_usdt = #{merchantPayOrder.haveUsdt}
 and owner_address like concat("%", #{merchantPayOrder.ownerAddress},"%")
order by  create_time desc

select address, merchant_id, hex_address, return_address, private_key, private_key_base, trx_balance, usdt_balance, merchant_order_no, create_time, have_usdt, pay_address, order_no, message, owner_address, id from merchant_pay_order
where  merchant_id = #{merchantPayOrder.merchantId}
 and address = #{merchantPayOrder.address}
 and return_address = #{merchantPayOrder.returnAddress}
 and merchant_order_no = #{merchantPayOrder.merchantOrderNo}
 and have_usdt = #{merchantPayOrder.haveUsdt}
 and pay_address = #{merchantPayOrder.payAddress}
order by  create_time desc

select IFNULL(sum(merchant_pay_address.order_amount),0) askSuccessAmount, IFNULL(sum(
IFNULL(merchant_pay_address.received_amount,0) ),0) paySuccessAmount, IFNULL(sum( IFNULL(merchant_pay_address.fee_amount,0)
),0) feeAmount, count(0) orderCount, IFNULL(count(merchant_pay_address.received_amount),0) paySuccessCount
from merchant_pay_address where merchant_pay_address.merchant_id = #{id} and TO_DAYS(merchant_pay_address.create_time ) = TO_DAYS(NOW())

select IFNULL(sum(merchant_pay_address.order_amount),0) askSuccessAmount, IFNULL(sum(
IFNULL(merchant_pay_address.received_amount,0) ),0) paySuccessAmount, IFNULL(sum( IFNULL(merchant_pay_address.fee_amount,0)
),0) feeAmount, count(0) orderCount, IFNULL(count(merchant_pay_address.received_amount),0) paySuccessCount
from merchant_pay_address where merchant_pay_address.merchant_id = #{id}
